mapper.update:
UPDATE article
SET pageView=pageView + 1
WHERE id = #{aid}

UPDATE article
SET title=#{title},
mdContent=#{mdContent},
htmlContent=#{htmlContent},
summary=#{summary},
cid=#{cid},
editTime=#{editTime}

    ,state=1


    ,publishDate=#{publishDate}

WHERE id=#{id}

mapper.select:
SELECT a.`id`,a.`title`,a.`editTime`,a.`pageView`,a.`state`,u.`nickname`,c.`cateName`,a.`uid`
FROM article a,user u,category c
WHERE a.`cid`=c.`id` AND a.`uid`=u.`id`

    AND a.`uid`=#{uid}


    AND a.`state`=#{state}


    AND a.`state`=1


    AND title LIKE concat('%',#{keywords},'%')

ORDER BY a.editTime
DESC
LIMIT #{start},#{count};

SELECT a.`id`,a.`title`,a.`editTime`,a.`pageView`,a.`state`,u.`nickname`,c.`cateName`,a.`uid` FROM article a,user u,category c WHERE a.`cid`=c.`id` AND a.`uid`=u.`id` AND a.state=1
AND title LIKE concat('%',#{keywords},'%')
ORDER BY a.editTime DESC LIMIT #{start},#{count};

SELECT count(*)
FROM article


        AND state=#{state}


        AND uid=#{uid}


        AND title LIKE concat('%',#{keywords},'%')

mapper.update:
